 redirect(base_url().'debit_card/add_choose_account/error');                }            }        }else {            $this->session->set_flashdata('error_message', '1');            redirect(base_url().'backdoor/dashboard');        }    }    public function edit_choose_account($msg=''){        if ($this->session->userdata('email_address')) {            if ($msg == 'success') {                $data['feedback'] = '<div id="message"  class="text-center alert alert-success">Successfully Updated !!</div>';            } else if ($msg == 'error') {                $data['feedback'] = '<div id="message" class=" text-center alert alert-danger">Problem to Update !!</div>';            }            $this->form_validation->set_rules('txtAccountType', 'Choose Account', 'trim|required');            if ($this->form_validation->run() == FALSE){                $data['title'] = "Debit Card";                $this->load->view('admin/block/header',$data);                $this->load->view('admin/block/left_nav');                $this->load->view('admin/debit_card/edit_choose_account');                $this->load->view('admin/block/footer');            }else{                $this->Common_model->data = array('account_name' => htmlentities($this->input->post('txtAccountType')),  'modified_by'=>$this->session->userdata('admin_user_id'));                $this->Common_model->table_name = 'debit_card_choose_account';                $this->Common_model->where = array('id' => $this->input->post('txtAccountTypeId'));                $result = $this->Common_model->update();                if ($result) {                    redirect(base_url().'debit_card/edit_choose_account/success');                } else {                    redirect(base_url().'debit_card/edit_choose_account/error');                }            }        }else {            $this->session->set_flashdata('error_message', '1');            redirect(base_url().'backdoor/dashboard');        }    }   